import React, { useState, useEffect } from 'react';
import { createNewUser, getLoginUser } from '../api';


const Login = ({setIsLoggedIn, isLoggedIn}) => {

    const [newUser, setNewUser] = useState(false)
    const [newUserIdPass, setNewUserIdPass] = useState({})

    return (
        isLoggedIn === false
        ? (
            <div className='not-logged-in-splash'>

                {/* check if we need to create a new user ---> */}
                <p id='new-sign-up-option'>New User? 
                    <span>
                        <input type="checkbox" 
                            id='create-new-user'
                            onChange={(event) => {
                                setNewUser(true)
                                console.log(newUser)
                            }} />
                    </span>
                </p>

                <form id='login-form'>
                    <label htmlFor='username-input'>Username:</label>
                    <span id='username-input'>
                    <input id='username'
                        onChange={(event) => {
                            (console.log('typing username', event.target.value))
                            setNewUserIdPass()
                        }}
                    />
                    </span>
                    <label htmlFor='password-input'>Password:</label>
                    <span id='password-input'>
                    <input type="password" id='password'></input>
                    </span>

                {/* if we aren't creating a new user, we call the API with a dif. function --> */}

                    <label htmlFor='submit-login'>
                        <button type='submit'
                        onClick={
                            newUser === true 
                            ? (
                                // run POST /api/COHORT-NAME/users/login
                                console.log('nice')
                            ) : (
                                // run POST /api/COHORT-NAME/users/register
                                createNewUser(newUserIdPass.user, newUserIdPass.pass)
                            )
                        }
                        >Submit
                        </button>
                    </label>
                </form>
            </div>
        ) : null
    )
}

export default Login
