export const createNewUser = async(username, password) => {
    let BaseURL = 'https://strangers-things.herokuapp.com/api/2207-ftb-et-web-pt/'
    let response = await fetch(`${BaseURL}/users/register`, {
        method: "POST",
        headers: {
        'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            user: {
            username: username,
            password: password
        }
  })
    }).then(results => results.json())
    console.log(response)
}

export const getLoginUser = async(username, password) => {
    let BaseURL = 'https://strangers-things.herokuapp.com/api/2207-ftb-et-web-pt/'
    let response = await fetch(`${BaseURL}/users/login`, {
        method: "POST",
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          user: {
            username: 'superman27',
            password: 'krypt0n0rbust'
          }
        })
      }).then(response => response.json())
        .then(result => {
          console.log(result);
        })
        .catch(console.error);
}

export const getAPIPosts = async() => {
    try {
        const BaseURL = 'https://strangers-things.herokuapp.com/api/2207-ftb-et-web-pt/'
        const response = await fetch(`${BaseURL}posts`).then(results => results.json())
        console.log(response.data.posts)
        return (response.data.posts)
    } catch (error) {
        console.log(error)
    }
}
